CPF <- c(5,6,2,7,3,8,6,9,5,9,6)

VV1 <- c(10,9,8,7,6,5,4,3,2,0,0)

RES1 <- CPF * VV1

m <- matrix(c(CPF, VV1, RES1), ncol = 11, byrow = T)
rownames(m) <- c("CPF: ", "VV 1: ", "RES: ")
knitr::kable(m)

